import React from "react";
import styled from "styled-components";
import { useMocks } from "../context/mock-context";
const Menu = props => {
  const { menu } = useMocks();
  return (
    <MenuDesktop>
      {menu.map(item => (
        <Item>{item.item}</Item>
      ))}
    </MenuDesktop>
  );
};
export default Menu;

const MenuDesktop = styled.ul`
  display: flex;
  justify-content: space-around;
`;

const Item = styled.li`
  list-style-type: none;
`;
