import React, { useRef, useEffect } from "react";
import styled from "styled-components";
import { useMocks } from "../context/mock-context";
import { Wrapper } from "../styled/space";
import { ManifestLabor, MediumSubtitle } from "../styled/typos";

const Manifeste = () => {
  const { manifeste } = useMocks();
  const manifestLaborRef = useRef(null);
  const titleRef = useRef(null);

  useEffect(() => {
    const givePositionToFix = () => {
      const laborTop = manifestLaborRef.current.getBoundingClientRect().top;
      console.log();
      titleRef.current.style.top =
        laborTop + titleRef.current.offsetWidth / 2 + "px";
    };
    givePositionToFix();
  });

  return (
    <Wrapper>
      <FirstPart>
        <Fixed>
          <MediumSubtitle ref={titleRef}>{manifeste.title}</MediumSubtitle>
        </Fixed>
        <ManifestLabor ref={manifestLaborRef}>
          {manifeste.content}
        </ManifestLabor>
      </FirstPart>
    </Wrapper>
  );
};
export default Manifeste;

const Fixed = styled.div`
  width: 30%;
  position: relative;

  h4 {
    transform: rotate(-90deg);
    position: fixed;
    margin-left: 6%;
  }
`;
const FirstPart = styled.div`
  padding-top: 150px;
  padding-bottom: 180px;
  position: relative;
  display: flex;
  justify-content: space-around;
  flex-direction: row;
  background-color: ${props => props.theme.colors.black};
  p {
    width: 100%;
  }
`;
