import React, { useState } from "react";
import styled from "styled-components";
import { Collapse } from "react-collapse";
import { ExpertiseTitle, Labor, Subtitle } from "../styled/typos";
import { FatWrapper } from "../styled/space";

const Accordion = props => {
  const [isOpened, setOpened] = useState(false);
  const [expertiseActive, setExpertiseActive] = useState("engagement-societal");

  const handleExpertise = slug => {
    setExpertiseActive(slug);
    setOpened(!isOpened);
  };
  return (
    <FatWrapper>
      <Container>
        <Titles>
          <ExpertiseSubtitle>{props.subTitle}</ExpertiseSubtitle>
          {props.content.map(el => {
            return (
              <ExpertiseTitle
                className={el.slug === expertiseActive && "isActive"}
                onClick={() => handleExpertise(el.slug)}
                key={el.title}
              >
                {el.title}
              </ExpertiseTitle>
            );
          })}
        </Titles>

        <ShowOnMobile>
          <Collapse isOpened={isOpened}>
            <div>Random content</div>
          </Collapse>
        </ShowOnMobile>
        <Content>
          <ExpertiseSubtitle>{props.subTitleTwo}</ExpertiseSubtitle>
          <HideOnMobile>
            {props.content.map(el => {
              if (el.slug === expertiseActive) {
                return <Labor>{el.description}</Labor>;
              }
            })}
          </HideOnMobile>
        </Content>
      </Container>
    </FatWrapper>
  );
};
export default Accordion;

const Container = styled.div`
  display: flex;
  justify-content: space-around;
`;

const ExpertiseSubtitle = styled(Subtitle)`
  margin-bottom: 25px;
`;

const ShowOnMobile = styled.div`
  display: none;
  ${props => props.theme.medias.medium`
  display:block;
`}
`;

const Titles = styled.div`
  width: 50%;
`;

const Content = styled.div`
  width: 30%;
`;

const HideOnMobile = styled.div`
  display: block;
  ${props => props.theme.medias.medium`
  display:none;
`}
`;
