import React, { useRef, useEffect } from "react";
import styled from "styled-components";
import { useMocks } from "../context/mock-context";
import { Wrapper } from "../styled/space";
import {
  ManifestLabor,
  MediumSubtitle,
  ArticleTitle,
  Chapeau,
  Labor,
  ArticleInterTitle
} from "../styled/typos";
import ArticleHeader from "../components/articleHeader";

const Manifeste = () => {
  const { manifeste } = useMocks();
  const manifestLaborRef = useRef(null);
  const titleRef = useRef(null);

  useEffect(() => {
    const fixEl = () => {
      const laborTop = manifestLaborRef.current.getBoundingClientRect().top;

      titleRef.current.style.top =
        laborTop + titleRef.current.offsetWidth / 2 + "px";
    };

    console.log(window.scrollY);
    console.log(manifestLaborRef.current.getBoundingClientRect());

    fixEl();
  }, []);

  return (
    <>
      <Wrapper>
        <FirstPart>
          <Fixed>
            <MediumSubtitle ref={titleRef}>
              {manifeste.firstPart.subtitle}
            </MediumSubtitle>
          </Fixed>
          <ManifestLabor ref={manifestLaborRef}>
            {manifeste.firstPart.content}
          </ManifestLabor>
        </FirstPart>
      </Wrapper>
      <Wrapper isWhite>
        <SecondPart>
          <MediumSubtitle isBlack>
            {manifeste.secondPart.subtitle}
          </MediumSubtitle>
          <ArticleTitle>{manifeste.secondPart.title}</ArticleTitle>
          <ArticleHeader
            media={manifeste.secondPart.media}
            authors={manifeste.secondPart.authors}
            published={manifeste.secondPart.published}
          />
          <ArticleSection>
            <CustomChapeau isBlack>
              {manifeste.secondPart.sections[0].title}
            </CustomChapeau>
            <CustomLabor>
              {manifeste.secondPart.sections[0].content}
            </CustomLabor>
          </ArticleSection>

          {manifeste.secondPart.sections.map((section, i) => {
            return (
              i > 0 && (
                <ArticleSection key={i}>
                  <ArticleInterTitle>{section.title}</ArticleInterTitle>
                  <Labor>{section.content}</Labor>
                </ArticleSection>
              )
            );
          })}
        </SecondPart>
      </Wrapper>
    </>
  );
};
export default Manifeste;

const CustomLabor = styled(Labor)`
  width: 50%;
`;
const CustomChapeau = styled(Chapeau)`
  width: 35%;
`;
const ArticleSection = styled.div`
  display: flex;
  justify-content: space-between;
  flex-direction: row;
  padding: 70px 0;
`;

const Fixed = styled.div`
  width: 30%;
  position: relative;

  h4 {
    transform: rotate(-90deg);
    position: fixed;
    margin-left: 6%;
  }
`;
const FirstPart = styled.div`
  padding-top: 150px;
  padding-bottom: 180px;
  position: relative;
  display: flex;
  justify-content: space-around;
  flex-direction: row;
  background-color: ${props => props.theme.colors.black};
  p {
    width: 100%;
  }
`;

const SecondPart = styled.div`
  padding: 120px 0;
  background-color: ${props => props.theme.colors.white};
`;
