import React from "react";
import styled from "styled-components";

import { useMocks } from "../context/mock-context";

import { Navigation } from "../styled/typos";

const Menu = props => {
  const { menu } = useMocks();
  return (
    <MenuDesktop>
      <Logo />
      {menu.map(item => (
        <Navigation>{item.item}</Navigation>
      ))}
    </MenuDesktop>
  );
};
export default Menu;

const Logo = styled.img`
  background-image: url("pictos/logo.svg");
  height: 50px;
  width: 110px;
`;

const MenuDesktop = styled.ul`
  display: flex;
  justify-content: flex-end;
  position: absolute;
  width: 100%;
`;
