import React from "react";
import styled from "styled-components";
import {
  SmallSubtitle,
  FatTitle,
  Labor,
  SmallNavigation
} from "../styled/typos";
const member = props => {
  const { dataMember } = props;
  console.log(props.even);

  const MemberSmallSubtitle = styled(SmallSubtitle)`
    padding: ${props => (props.withWrapper ? "0 20%" : "0")};
    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};
    margin: ${props => (props.insideDescription ? " 50px 0 25px 0" : 0)};
  `;
  const Container = styled.div`
    padding-top: 125px;
    position: relative;
    background-color: ${props.even
      ? props => props.theme.colors.white
      : props => props.theme.colors.black};
    flex-direction: ${props.even ? "row" : "row-reverse"};
  `;
  const Line = styled.div`
    border: 0.5px solid
      ${props.even
        ? props => props.theme.colors.black
        : props => props.theme.colors.white};

    width: 1px;
    height: 115px;
    position: relative;
    margin-left: 25%;
    margin-bottom: 20px;
  `;

  const MemberLabor = styled(Labor)`
    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};
  `;

  const MemberFatTitle = styled(FatTitle)`
    padding: 0 20%;
    margin-top: 10px;
    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};
  `;

  const MemberHr = styled.hr`
    width: 100%;
    border: 0.5px solid
      ${props.even
        ? props => props.theme.colors.black
        : props => props.theme.colors.white};
    margin: 50px 0 25px 10px;
  `;
  const Content = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-direction: ${props.even ? "row-reverse" : "row"};
  `;

  const Description = styled.div`
    padding-right: 10%;
    width: 100%;
  `;
  const Socials = styled.div`
    width: 40%;
    display: flex;
    flex-direction: column;
  `;
  const Social = styled(SmallNavigation)`
    border: 1px solid
      ${props.even
        ? props => props.theme.colors.black
        : props => props.theme.colors.white};
    border-radius: 100%;
    margin-bottom: 25px;

    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};

    display: inline-block;
    width: 30px;
    height: 30px;
    text-align: center;
    vertical-align: middle;
  `;
  return (
    <Container>
      <MemberSmallSubtitle withWrapper>
        {dataMember.subTitle}
      </MemberSmallSubtitle>
      <MemberFatTitle>{dataMember.name}</MemberFatTitle>
      <Line />

      <Flex></Flex>
      <Content>
        <Img src={dataMember.img} alt={dataMember.name} />
        <Socials>
          <Social>{dataMember.socials.twitter && "Tw"}</Social>
          <Social>{dataMember.socials.linkedin && "Li"}</Social>
        </Socials>
        <Description>
          <MemberLabor>{dataMember.description}</MemberLabor>
          <Flex>
            <MemberSmallSubtitle insideDescription>
              {dataMember.engagements.subTitle}
            </MemberSmallSubtitle>
            <MemberHr />
          </Flex>

          <MemberLabor>{dataMember.engagements.description}</MemberLabor>
        </Description>
      </Content>
    </Container>
  );
};

export default member;

const Img = styled.img`
  width: 520px;
`;
const Flex = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
`;
