import React from "react";
import styled from "styled-components";
import { useRouter } from "next/router";
import Link from "next/link";

import { useMocks } from "../context/mock-context";

import { Navigation } from "../styled/typos";

const Menu = props => {
  const { menu } = useMocks();
  const router = useRouter();
  const currentPage = router.pathname;
  const currentTitle = router.query.title;
  console.log(router);
  return (
    <MenuDesktop>
      <Link href="/">
        {currentPage === "/" ? (
          <Logo src="pictos/logo.svg" />
        ) : (
          <Flex>
            <MinimalLogo src="pictos/minimal_logo.svg" />
            <Navigation>{currentTitle}</Navigation>
          </Flex>
        )}
      </Link>

      <Items>
        {menu.map(item => (
          <Link
            href={{ pathname: `${item.path}`, query: { title: item.item } }}
            key={item.item}
          >
            <Navigation className={currentPage === item.path && "isActive"}>
              {item.item}
            </Navigation>
          </Link>
        ))}
      </Items>
    </MenuDesktop>
  );
};
export default Menu;

const Logo = styled.img`
  height: 40px;
  cursor: pointer;

  ${props => props.theme.medias.medium`
    transition: all 500ms;
    height: 30px;
   `}
`;

const Flex = styled.div`
  display: flex;
  color: ${props => props.theme.colors.white};
  p {
    margin-left: 5px;
  }
`;

const MinimalLogo = styled.img`
  height: 35px;
  width: auto;
  top: 5px;
  position: relative;
  cursor: pointer;

  ${props => props.theme.medias.medium`
    transition: all 500ms;
    height: 30px;
    width: 68px;
   `}
`;

const Items = styled.div`
  display: flex;
  ${props => props.theme.medias.medium`
    display: none;
   `}
`;
const MenuDesktop = styled.div`
  display: flex;
  justify-content: space-between;
  position: absolute;
  width: 100%;
  padding-top: 35px;
  padding-left: 10%;
  z-index: 10;
`;
