import { css, createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`

   @font-face {
    font-family: 'Source Sans Pro';
    font-weight: light;
    src:
      local('Source Sans Pro'),
      local('SourceSansPro-Light'),
      url('./../../static/fonts/SourceSansPro-Light.ttf');
   }

   @font-face {
    font-family: 'Source Sans Pro';
    font-weight: bold;
    src:
      local('Source Sans Pro'),
      local('SourceSansPro-Bold'),
      url('./../../static/fonts/SourceSansPro-Bold.ttf');
   }
     @font-face {
    font-family: 'Source Sans Pro';
    font-weight: 600;
    src:
      local('Source Sans Pro'),
      local('SourceSansPro-SemiBold'),
      url('./../../static/fonts/SourceSansPro-SemiBold.ttf');
   }

   @font-face {
    font-family: 'Source Sans Pro';
    font-weight: normal;
    src:
      local('Source Sans Pro'),
      local('SourceSansPro-Regular'),
      url('./../../static/fonts/SourceSansPro-Regular.ttf');
   }

   html {
      box-sizing: border-box;
    }
    body {
      /* background-color: ${props => props.theme.colors.base.background};
      color: ${props => props.theme.colors.base.text};
      font-family: ${props => props.theme.fonts.fontFamily};
      font-size: ${props => props.theme.dimensions.fontSize}; */
      font-weight: 400;
      line-height: 24px;
      margin: 0;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }
    html,
    body {
      min-height: 100%;
    }
    *,
    *:before,
    *:after {
      box-sizing: inherit;
    }
    :root {
      -moz-tab-size: 4;
      tab-size: 4;
    }
`;

export default GlobalStyle;
