import styled from "styled-components";

export const Wrapper = styled.div`
  padding: 0 10%;
  margin: 0 auto;
  width: 100%;
  left: 0;
  right: 0;
`;

export const FatWrapper = styled.div`
  padding: ${props => (props.wrapperOnMobile ? "0" : "0 20%")};
  margin: 0 auto;
  width: 100%;
  left: 0;
  right: 0;

  ${props => props.theme.medias.medium`
    padding:${props => (props.noWrapperOnMobile ? "0" : "0 10%")};

  `}
`;
