import React from "react";
import styled from "styled-components";
import { SmallSubtitle, FatTitle, Labor } from "../styled/typos";
const member = props => {
  const { dataMember } = props;
  console.log(props.even);

  const MemberSmallSubtitle = styled(SmallSubtitle)`
    padding: ${props => (props.withWrapper ? "0 20%" : "0 0 0 10%")};
    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};
  `;
  const Container = styled.div`
    position: relative;
    background-color: ${props.even
      ? props => props.theme.colors.white
      : props => props.theme.colors.black};
    flex-direction: ${props.even ? "row" : "row-reverse"};
  `;
  const Line = styled.div`
    border: 0.5px solid
      ${props.even
        ? props => props.theme.colors.black
        : props => props.theme.colors.white};
    transform: rotate(90deg);
    width: 115px;
  `;

  const MemberLabor = styled(Labor)`
    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};
  `;

  const MemberFatTitle = styled(FatTitle)`
    padding: 0 20%;
    color: ${props.even
      ? props => props.theme.colors.black
      : props => props.theme.colors.white};
  `;

  const MemberHr = styled.hr`
    width: 100%;
    border: 0.5px solid
      ${props.even
        ? props => props.theme.colors.black
        : props => props.theme.colors.white};
    margin-left: 20px;
  `;
  const Content = styled.div`
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-direction: ${props.even ? "row-reverse" : "row"};
  `;
  return (
    <Container>
      <MemberSmallSubtitle withWrapper>
        {dataMember.subTitle}
      </MemberSmallSubtitle>
      <MemberFatTitle>{dataMember.name}</MemberFatTitle>
      <Line />

      <Flex></Flex>
      <Content>
        <Img src={dataMember.img} alt={dataMember.name} />
        <div>
          <MemberLabor>{dataMember.description}</MemberLabor>
          <Flex>
            <MemberSmallSubtitle>
              {dataMember.engagements.subTitle}
            </MemberSmallSubtitle>
            <MemberHr />
          </Flex>

          <MemberLabor>{dataMember.engagements.description}</MemberLabor>
        </div>
      </Content>
    </Container>
  );
};

export default member;

const Img = styled.img`
  width: 520px;
`;
const Flex = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
`;
