import React, { useRef } from "react";
import styled, { keyframes } from "styled-components";

const isOpening = keyframes`
  0% {
    transform:translateX(100%);
  }
  100% {
     transform:translateX(0);
  }
`;

const isClosing = keyframes`
  0% {
    transform:translateX(0);
  }
  100% {
     transform:translateX(100%);
  }
`;

const MenuMobile = props => {
  const menuContainer = useRef(null);
  const handleClick = e => {
    menuContainer.current.style.transform = "translateX(100%)";
    setTimeout(e => {
      props.onClick(e);
    }, 6000);
  };
  const Container = styled.div`
    position: absolute;
    width: 70%;
    height: 100vh;
    right: 0;
    top: 0;
    padding: 35px;
    background-color: ${props => props.theme.colors.white};
    transition: all 500ms ease-in-out;
    animation: ${isOpening} 500ms ease-in-out;
  `;
  if (!props.isOpen) {
    return null;
  }

  return (
    <Container ref={menuContainer}>
      <Flex>
        <Logo src="pictos/logo_mobile.svg" />
        <Close src="pictos/close_mobile.svg" onClick={() => handleClick()} />
      </Flex>
    </Container>
  );
};

export default MenuMobile;

const Flex = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  align-items: center;
`;
const Logo = styled.img`
  width: 68px;
`;

const Close = styled.img`
  width: 13px;
  right: 2px;
  position: relative;
  cursor: pointer;
`;
