import React, { useState } from "react";
import styled from "styled-components";
import { Collapse } from "react-collapse";
import { ExpertiseTitle, Labor, Subtitle } from "../styled/typos";

const Accordion = props => {
  const [isOpened, setOpened] = useState(false);
  const [expertiseActive, setExpertiseActive] = useState("engagement-societal");

  const handleExpertise = slug => {
    setExpertiseActive(slug);
    setOpened(!isOpened);
  };
  return (
    <Container>
      <div>
        <Subtitle>{props.subTitle}</Subtitle>
        {props.content.map(el => {
          return (
            <ExpertiseTitle
              onClick={() => handleExpertise(el.slug)}
              key={el.title}
            >
              {el.title}
            </ExpertiseTitle>
          );
        })}
      </div>

      <ShowOnMobile>
        <Collapse isOpened={isOpened}>
          <div>Random content</div>
        </Collapse>
      </ShowOnMobile>
      <div>
        <Subtitle>{props.subTitleTwo}</Subtitle>
        <HideOnMobile>
          {props.content.map(el => {
            if (el.slug === expertiseActive) {
              return el.description;
            }
          })}
          <Labor>{}</Labor>
        </HideOnMobile>
      </div>
    </Container>
  );
};
export default Accordion;

const Container = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
`;
const ShowOnMobile = styled.div`
  display: none;
  ${props => props.theme.medias.medium`
  display:block;
`}
`;

const HideOnMobile = styled.div`
  display: block;
  ${props => props.theme.medias.medium`
  display:none;
`}
`;
