import { useEffect, useState } from "react";
import Head from "next/head";

import styled from "styled-components";
import { Subtitle, FatTitle } from "../styled/typos";
import { Wrapper } from "../styled/space";

import { useMocks } from "../context/mock-context";

const client = require("contentful").createClient({
  space: "86i03dw6wwwc",
  accessToken: "fs65wT3qwmrz2Rcyh0fFkJE2uukw1N5mxY9_IzljpH0"
});

function Proposition() {
  const { proposition } = useMocks();

  return (
    <Container>
      <Head>
        <title>Societer | la Proposition</title>
      </Head>
      <BackgroundImage />
      <Wrapper>
        <Titles>
          <Subtitle>{proposition.subtitle}</Subtitle>
          <FatTitle>{proposition.title}</FatTitle>
        </Titles>
      </Wrapper>
    </Container>
  );
}

export default Proposition;

const Container = styled.div`
  height: 100vh;
`;

const BackgroundImage = styled.div`
  background-image: url("images/home/bkg_prehome.jpg");
  width: 100%;
  height: 100vh;
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  position: absolute;
  top: 0;
`;

const Titles = styled.div`
  width: 100%;
  z-index: 10;
  position: absolute;
  top: 50%;
  h1 {
    width: 50%;
  }
  h3 {
    color: ${props => props.theme.colors.white};
  }
`;

