import { useEffect, useState } from "react";
import Head from "next/head";

import styled from "styled-components";
import { Subtitle, FatTitle } from "../styled/typos";
import { useMocks } from "../context/mock-context";

const client = require("contentful").createClient({
  space: "86i03dw6wwwc",
  accessToken: "fs65wT3qwmrz2Rcyh0fFkJE2uukw1N5mxY9_IzljpH0"
});

function HomePage() {
  const { homepage } = useMocks();

  return (
    <Container>
      <Head>
        <title>Societer | Sustanable leadership</title>
      </Head>
      <BackgroundImage />
      <Titles>
        <Subtitle>{homepage.subtitle}</Subtitle>
        <FatTitle>{homepage.title}</FatTitle>
      </Titles>
    </Container>
  );
}

export default HomePage;

const Container = styled.div`
  height: 100vh;
  position: relative;
`;

const BackgroundImage = styled.div`
  background-image: url("images/home/bkg_prehome.jpg");
  width: 100%;
  height: 100vh;
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
`;

const Titles = styled.div`
  width: 100%;
  z-index: 10;
  border: 1px solid red;
  position: absolute;
  top: 65%;
